import React from "react";
import { Card } from "./Weather.styles";
import { StyledItem } from "./Weather.styles";

const Weather = ({ weatherData }) => {
  const weather = weatherData.weather[0];
  return (
    <>
      <h2>
        The Forecast for {weatherData.name} for today{" "}
        {new Date().toLocaleString()} is:
      </h2>
      <Card>
        <StyledItem>{weather?.main}</StyledItem>
        <StyledItem>
          <span>{weatherData?.main?.temp.toFixed()}°F</span>
        </StyledItem>
        <StyledItem>
          Real Feel: {weatherData?.main?.feels_like.toFixed()}°F
        </StyledItem>
      </Card>
    </>
  );
};

export default Weather;
